body.script:
//
  // SCRIPT PERLINDUNGAN WEBSITE (DITINGKATKAN)
  // 1. Toast Logic
  const toast = document.getElementById("custom-toast");
  const toastText = document.getElementById("toast-text");
  let timer;
  const animeQuotes = [
      "Yamete Kudasai! Jangan dicopy, Senpai~",
      "Omae wa mou... tidak bisa klik kanan!",
      "Dilarang mencuri! Itu tindakan villain!",
      "Ara ara~ Mau ngapain tuh?",
      "Nani?! Teknik copy-paste tidak mempan!",
      "Ho? Kamu mencoba mendekat untuk mencuri?"
  ];

  function showToast() {
      const randomIndex = Math.floor(Math.random() * animeQuotes.length);
      toastText.textContent = animeQuotes[randomIndex];
      toast.className = "show";
      clearTimeout(timer);
      timer = setTimeout(function(){ toast.className = toast.className.replace("show", ""); }, 3000);
  }

  // 2. Disable Right Click (Context Menu)
  document.addEventListener('contextmenu', function(e) {
      e.preventDefault();
      e.stopPropagation();
      showToast();
      return false;
  }, true);

  // 3. Disable Selection
  document.addEventListener('selectstart', function(e) {
      e.preventDefault();
      return false;
  });

  // 4. Disable Keyboard Shortcuts (F12, Ctrl+U, Ctrl+Shift+I/J/C)
  document.addEventListener('keydown', function(e) {
      // F12
      if (e.keyCode == 123) {
          e.preventDefault();
          e.stopPropagation();
          showToast();
          return false;
      }

      // Ctrl+Shift+I, Ctrl+Shift+J, Ctrl+Shift+C (DevTools)
      if (e.ctrlKey && e.shiftKey && (e.keyCode == 73 || e.keyCode == 74 || e.keyCode == 67)) {
          e.preventDefault();
          e.stopPropagation();
          showToast();
          return false;
      }

      // Ctrl+U (View Source)
      if (e.ctrlKey && e.keyCode == 85) {
          e.preventDefault();
          e.stopPropagation();
          showToast();
          return false;
      }

      // Ctrl+S (Save), Ctrl+P (Print)
      if (e.ctrlKey && (e.keyCode == 83 || e.keyCode == 80)) {
          e.preventDefault();
          e.stopPropagation();
          showToast();
          return false;
      }
  }, true); // Use Capture phase for higher priority

  // 5. Anti-Debugger (Optional - Makes inspect element harder)
  setInterval(function(){
      // Logic: Jika DevTools terbuka, debugger statement akan memberhentikan script
      // Ini tidak menutup DevTools tapi membuatnya sangat mengganggu
      (function(){})['constructor']('debugger')();
  }, 2000);
//

//
(function() {
    const btn = document.getElementById('mobile-floating-menu-btn');
    const menu = document.getElementById('mobile-menu-overlay');
    const targetList = document.getElementById('cloned-menu-list');
    let menuCopied = false;

    // Fungsi untuk meng-copy menu (Coba terus sampai berhasil)
    function copyMenu() {
        const originalList = document.querySelector('.PageList ul');

        if (originalList && originalList.children.length > 0) {
            targetList.innerHTML = originalList.innerHTML;

            // Bersihkan class/style bawaan agar tampilan mobile bersih
            const links = targetList.querySelectorAll('a');
            links.forEach(a => {
                a.removeAttribute('class');
                a.removeAttribute('style');
            });

            menuCopied = true;
            return true;
        }
        return false;
    }

    // Coba copy segera
    if(!copyMenu()) {
        // Jika gagal, cek ulang setiap 500ms (maksimal 10 detik)
        const checkInterval = setInterval(() => {
            if (copyMenu()) {
                clearInterval(checkInterval); // Berhenti jika sudah berhasil
            }
        }, 500);

        // Hentikan pengecekan paksa setelah 10 detik untuk hemat memori
        setTimeout(() => clearInterval(checkInterval), 10000);
    }

    // Event Listener Tombol (Lebih responsif daripada onclick html)
    if(btn && menu) {
        btn.addEventListener('click', function(e) {
            e.stopPropagation(); // Mencegah bubbling

            // Jika menu belum ter-copy saat diklik (koneksi lambat), coba copy lagi sekarang
            if (!menuCopied) copyMenu();

            // Toggle tampilan
            menu.classList.toggle('active');

            // Ganti Icon
            if (menu.classList.contains('active')) {
                btn.innerHTML = '<svg viewBox="0 0 24 24"><path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" fill="currentColor"/></svg>';
            } else {
                btn.innerHTML = '<svg viewBox="0 0 24 24"><path d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z" fill="currentColor"/></svg>';
            }
        });

        // Tutup jika klik di luar
        document.addEventListener('click', function(e) {
            if (!menu.contains(e.target) && !btn.contains(e.target)) {
                menu.classList.remove('active');
                // Reset icon ke hamburger
                btn.innerHTML = '<svg viewBox="0 0 24 24"><path d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z" fill="currentColor"/></svg>';
            }
        });
    }
})();
