document.addEventListener("DOMContentLoaded", function() {
    const pager = document.getElementById("blog-pager");
    if (!pager) return;

    // Ambil Link Asli Blogger
    const newerLink = document.getElementById("blog-pager-newer-link");
    const olderLink = document.getElementById("blog-pager-older-link");

    let newerUrl = newerLink ? newerLink.getElementsByTagName("a")[0].href : null;
    let olderUrl = olderLink ? olderLink.getElementsByTagName("a")[0].href : null;

    // Logika Halaman (Menggunakan Session Storage untuk simulasi angka)
    let currentPage = 1;
    const isHome = window.location.pathname === '/' || window.location.pathname === '/index.html';

    if (isHome) {
        sessionStorage.setItem('ngorand_page', 1);
        currentPage = 1;
    } else {
        // Ambil dari session jika ada, atau default ke 2 jika tidak diketahui
        let savedPage = parseInt(sessionStorage.getItem('ngorand_page'));
        if (savedPage) {
            currentPage = savedPage;
        } else {
            // Fallback jika user langsung ke halaman dalam tanpa lewat home
            currentPage = 2; // Asumsi aman
        }
    }

    // Hapus Konten Lama
    pager.innerHTML = "";

    // Buat Tombol PREV [ < ]
    if (newerUrl) {
        const prevBtn = document.createElement("a");
        prevBtn.href = newerUrl;
        prevBtn.className = "pager-btn";
        prevBtn.innerHTML = ""; // Simbol Panah Kiri
        prevBtn.title = "Halaman Sebelumnya";
        prevBtn.onclick = function() {
            // Decrement page number
            sessionStorage.setItem('ngorand_page', currentPage > 1 ? currentPage - 1 : 1);
        };
        pager.appendChild(prevBtn);
    }

    // Buat Angka Tengah [ 1 ]
    const numDisplay = document.createElement("span");
    numDisplay.className = "pager-num";
    numDisplay.textContent = currentPage;
    pager.appendChild(numDisplay);

    // Buat Tombol NEXT [ > ]
    if (olderUrl) {
        const nextBtn = document.createElement("a");
        nextBtn.href = olderUrl;
        nextBtn.className = "pager-btn";
        nextBtn.innerHTML = ""; // Simbol Panah Kanan
        nextBtn.title = "Halaman Selanjutnya";
        nextBtn.onclick = function() {
            // Increment page number
            sessionStorage.setItem('ngorand_page', currentPage + 1);
        };
        pager.appendChild(nextBtn);
    }
});
